function main = AMICprintf('Iniciando SO\n');printf('Memoria Total: 4 GB. Ocupada por SO: 1 GB. Inicnado Virus A, B y C\n');opcionPrincipal = menu("Elija una opcion",                          "Saber memoria ocupada segun tiempo",                          "Tiempo necesario para llenar la memoria",                          "Finalizar");switch opcionPrincipal  case{1}    t = input('Cuantos minutos pasaron desde el inicio?');    x = cantidadEspacioOcupado(t)    printf ('Ya se ocuparon %d KBs del SO\n', x);      case{2}    x = memoriaLLenaEn()    printf('Se necesitan %d minutos para llenar la memoria', x);                                                            endswitchendfunction